package Loops;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		
		while(i<=100) {
			
			System.out.println(i);
			
			i++;
			
		}
			
		System.out.println("====");
		
		i=100;
		
		while(i>0) {
			
			i--;
			System.out.println(i);
		}
		
		i=0;
		
		String[] name = {"veena","venkat","suresh","siva","Naresh"};
		
		while(i<name.length) {
			System.out.println(name[i]);
			i++;
		}
	
		}
	}


