package Operator;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		
		int b=10;
		
		a=a+4;
		
		System.out.println(a);//14
		
		a=4+a;
		
		System.out.println(a);//18
		
		a+=4;//a=a+4;
		
		System.out.println(a);//22
		
		a-=4;
		
		System.out.println(a);//18
		
		

	}

}
